mapper.insert:
INSERT INTO
  

  topicID,optionID,votetime,account,


  #{topicID, jdbcType=INTEGER},#{optionID, jdbcType=INTEGER},#{votetime, jdbcType=TIMESTAMP},#{account, jdbcType=VARCHAR}

mapper.select:
SELECT
  
FROM
  
where DATE_FORMAT(votetime,'%Y%m%d') = DATE_FORMAT(NOW(),'%Y%m%d')
AND account = #{account, jdbcType=VARCHAR}
AND topicID = #{topicID, jdbcType=INTEGER}
